import './homeheader.css'
import data from './homeheader-content'
import { useParams } from 'react-router-dom'

export default function HomeHeader() {
    const { styleParam } = useParams();

    console.log(styleParam)

    let filteredData = data.find(item => item.style === styleParam);

    if (!filteredData) {
        filteredData = data.find(item => item.style === "women")
    }

    return (
        <div className="container__homeheader">
            <div className="container_content">
                <h2>{filteredData.content}</h2>
                <button className="btn">TAKE YOUR STYLE QUIZ</button>
            </div>
            <div className="container_image">
                <img src={filteredData.image1} className="fashion-image" alt={filteredData.image1Alt}/>
                <img src={filteredData.image2} className="fashion-image" alt={filteredData.image2Alt}/>
            </div>
        </div>
    )
}
