'use strict';

const Container = require('postcss-values-parser/lib/container');
const Node = require('postcss-values-parser/lib/node');

class Parenthesis extends Node {
  constructor (opts) {
    super(opts);
    this.type = 'paren';
    this.parenType = '';
  }
}

Container.registerWalker(Parenthesis);

module.exports = Parenthesis;
